Bud.data.AnnualProjectionStore = WebLight.extend(Bud.data.XmlStore, {

    budDetailStore: null,
    budProjectedDetailStore: null,
    budProjectedWORDetailStore: null,
    budProjectedCommentSotre: null,
    isAnnualizedStore: false,

    fscAccountStore: null,

    hcmHouseCode: -1,
    hcmJob: -1,
    fscYear: -1,
    periodStart: 1,
    periodEnd: 1,

    _loading: false,
    _loadedStoreCount: 0,
    _calculating: false,
    _totalRow: null,
    _addingTotalRow: false,

    constructor: function (config) {

        var me = this;

        var fields = [
            { name: 'fscAccount', type: 'float' },
            { name: 'title' },
            { name: 'isNegative', mapping: '@isNegative', type: 'bool' }
        ];

        for (var i = 1; i <= 13; i++) {
            fields.push({ name: 'budget' + i, type: 'float' },
                { name: 'projection' + i, type: 'float' },
                { name: 'variance' + i, type: 'float' },
                { name: 'worForecast' + i, type: 'float' },
                { name: 'varianceComment' + i });
        }

        fields.push({ name: 'budgetTotal', type: 'float' },
                { name: 'projectionTotal', type: 'float' },
                { name: 'worForecastTotal', type: 'float' });

        config = Ext.apply(config || {}, { record: 'item',
            idProperty: '@fscAccount',
            fields: fields
        });

        Bud.data.AnnualProjectionStore.superclass.constructor.call(this, config);

        me.on({
            add: { fn: this.calculateStore },
            remove: { fn: this.calculateStore },
            update: { fn: this.calculateStore }
        });

        me.budDetailStore = new Bud.data.BudDetailsStore();
        me.budProjectedCommentSotre = new Bud.data.BudProjectedCommentStore();
        me.budProjectedDetailStore = new Bud.data.BudProjectedDetailStore();
        me.budProjectedWORDetailStore = new Bud.data.BudProjectedWORDetailStore();

        me.budDetailStore.on('load', me.subStoreLoaded.createDelegate(this));
        me.budProjectedCommentSotre.on('load', me.subStoreLoaded.createDelegate(this));
        me.budProjectedDetailStore.on('load', me.subStoreLoaded.createDelegate(this));
        me.budProjectedWORDetailStore.on('load', me.subStoreLoaded.createDelegate(this));

    },

    load: function (hcmHouseCode, hcmJob, fscYear, periodStart, periodEnd) {
        var me = this;
        if (me._loading)
            return;

        me.hcmHouseCode = hcmHouseCode;
        me.hcmJob = hcmJob;
        me.fscYear = fscYear;
        me.periodStart = periodStart;
        me.periodEnd = periodEnd;

        me._loadedStoreCount = 0;

        me.budDetailStore.load(hcmHouseCode, hcmJob, fscYear);
        me.budProjectedCommentSotre.load(hcmHouseCode, hcmJob, fscYear);
        me.budProjectedDetailStore.load(hcmHouseCode, hcmJob, fscYear);
        me.budProjectedWORDetailStore.load(hcmHouseCode, hcmJob, fscYear);

        var initData = me.initDataRecord(0, 'Total');
        me._totalRow = this.newRecord(initData);
    },

    subStoreLoaded: function () {
        var me = this;

        me._loadedStoreCount += 1;

        if (me._loadedStoreCount == 4) {
            me._loading = false;
            me.suspendEvents(false);

            // me.removeAll(); // can't use!
            me.remove(me.getRange());

            me.populateStore();
            //me.calculateStore();
            // added at 2011/06/28
            this.sort('title', 'ASC');


            // identify _addingTotalRow, skip run updateTotals method
            me._addingTotalRow = true;
            me.add(me._totalRow);
            // ignore total row changes
            me.ignoreRecords(me._totalRow);
            me._addingTotalRow = false;
            // calculate totalrow, apply data summary to totalRow
            me.calculateStore();

            me.resumeEvents();

            me.fireEvent('load', me);

        }
    },

    initDataRecord: function (fscAccount, title) {
        var initData = { fscAccount: fscAccount,
            title: title,
            budgetTotal: 0,
            projectionTotal: 0,
            worForecastTotal: 0
        };
        for (var i = 1; i <= 13; i++) {
            initData['budget' + i] = 0;
            initData['projection' + i] = 0;
            initData['variance' + i] = 0;
            initData['worForecast' + i] = 0;
            initData['varianceComment' + i] = '';
        }
        return initData;
    },

    getByFscAccount: function (fscAccount) {
        var me = this;

        var records = me.queryBy(function (rec) {
            return rec.get('fscAccount') == fscAccount;
        });

        var record = records.get(0);

        if (!record) {
            var fscAccountRecord = me.fscAccountStore.getById(fscAccount);
            var title = !!fscAccountRecord ? fscAccountRecord.get('friendlyName') : String.format('Unknown "{0}"', fscAccount);
            var isNegative = !!fscAccountRecord ? fscAccountRecord.get('isNegative') : false;
            var initData = me.initDataRecord(fscAccount, title);

            record = me.newRecord(Ext.apply({ isNegative: isNegative }, initData));
            me.add([record]);
        }

        return record;
    },

    calculateStore: function () {

        var me = this;

        if (me._calculating || me._addingTotalRow)
            return;


        me.suspendEvents(false);
        me._calculating = true;

        var i = 0, j = 0;
        var total = new Ext.util.MixedCollection();
        for (i = 1; i <= 13; i++) {
            total.add('projection' + i, 0);
            total.add('budget' + i, 0);
            total.add('variance' + i, 0);
            total.add('worForecast' + i, 0);
        }

        for (i = 0; i < this.getCount() - 1; i++) {
            var record = this.getAt(i);
            var isNeg = record.get('isNegative');


            for (j = 1; j <= 13; j++) {

                var projection = isNeg ? (-record.get('projection' + j)) : record.get('projection' + j);
                var budget = isNeg ? (-record.get('budget' + j)) : record.get('budget' + j);
                var variance = isNeg ? (-record.get('variance' + j)) : record.get('variance' + j);
                var worForecast = isNeg ? (-record.get('worForecast' + j)) : record.get('worForecast' + j);

                total.replace('projection' + j, total.get('projection' + j) + projection || 0);
                total.replace('budget' + j, total.get('budget' + j) + budget || 0);
                total.replace('variance' + j, total.get('variance' + j) + variance || 0);
                total.replace('worForecast' + j, total.get('worForecast' + j) + worForecast || 0);
            }
        }

        // 2011/07/01
        //Total flip the sign. The rule for total is Always consider revenue related account are position since they are income, other fscacount will be negative since they are expense. 
        // And total should be revenue – expense. 
        for (i = 1; i <= 13; i++) {
            me._totalRow.set('projection' + i, -me.decimal2(total.get('projection' + i)));
            me._totalRow.set('budget' + i, -me.decimal2(total.get('budget' + i)));
            me._totalRow.set('variance' + i, -me.decimal2(total.get('variance' + i)));
            me._totalRow.set('worForecast' + i, -me.decimal2(total.get('worForecast' + i)));
        }

        this.each(function (record) {

            var projTotal = 0;
            var budgetTotal = 0;
            var worTotal = 0;

            for (i = 1; i <= 13; i++) {
                var proj = record.get('projection' + i) || 0;
                var bud = record.get('budget' + i) || 0;
                var variance = record.get('variance' + i) || 0;

                if (me.decimal2(proj - bud) != me.decimal2(variance))
                    record.set('variance' + i, me.decimal2(proj - bud));

                projTotal += record.get('projection' + i) || 0;
                budgetTotal += record.get('budget' + i) || 0;
                worTotal += record.get('worForecast' + i) || 0;
            }
            record.set('budgetTotal', me.decimal2(budgetTotal));
            record.set('projectionTotal', me.decimal2(projTotal));
            record.set('worForecastTotal', me.decimal2(worTotal));
        });
        me.resumeEvents();
        me._calculating = false;
    },

    decimal2: function (v) {
        if (v && typeof v == 'number')
            return Math.round(v * 100) / 100;
        return v;
    },

    populateStore: function () {

        var me = this;
        me._calculating = true;
        me.budDetailStore.each(function (r, index) {
            var record = me.getByFscAccount(r.get('fscAccount'));
            for (var i = 1; i <= 13; i++)
                record.set('budget' + i, r.get('period' + i));
        });

        me.budProjectedDetailStore.each(function (r, index) {
            var record = me.getByFscAccount(r.get('fscAccount'));
            for (var i = 1; i <= 13; i++)
                record.set('projection' + i, r.get('period' + i));
        });

        me.budProjectedWORDetailStore.each(function (r, index) {
            var record = me.getByFscAccount(r.get('fscAccount'));
            for (var i = 1; i <= 13; i++)
                record.set('worForecast' + i, r.get('period' + i));
        });

        me.budProjectedCommentSotre.each(function (r, index) {
            var record = me.getByFscAccount(r.get('fscAccount'));
            for (var i = 1; i <= 13; i++)
                record.set('varianceComment' + i, r.get('period' + i));
        });
        me._calculating = false;
    },

    submitChanges: function (callback) {
        var me = this;
        this.fireEvent('beforesubmit', this);

        var xml = ['<transaction id="1">'];

        Ext.each(this.getChangedRecords(), function (item, index) {
            if (item.get('fscAccount')) {
                /// add projected details
                xml.push('<budProjectedDetail');
                var data = { fscAccount: item.get('fscAccount') };
                me.addAttributes(data);
                for (var i = 1; i <= 13; i++)
                    data['period' + i] = item.get('projection' + i);
                for (key in data)
                    xml.push(String.format(' {0}="{1}"', key,
                    Ext.isDate(data[key]) ? Ext.util.Format.date(data[key], 'm/d/Y') : data[key]));
                xml.push('/>');

                /// add projected comments

                xml.push('<budProjectedComment');
                var data = { fscAccount: item.get('fscAccount') };
                me.addAttributes(data);
                for (var i = 1; i <= 13; i++)
                    data['period' + i] = item.get('varianceComment' + i);
                for (key in data)
                    xml.push(String.format(' {0}="{1}"', key,
                    Ext.isDate(data[key]) ? Ext.util.Format.date(data[key], 'm/d/Y') : data[key]));
                xml.push('/>');
            }
        }, this);

        xml.push('</transaction>');

        var postData = String.format('moduleId=bud&requestId={0}&requestXml={1}&&targetId=iiTransaction',
             this.getRequestId(), xml.join(''));

        jQuery.post(this.url, postData, function (data, status) {
            if (callback)
                callback(data, status);
            me.fireEvent('submit');
            me.changes = [];
        });
    },

    addAttributes: function (data) {
        Ext.apply(data, { hcmHouseCode: this.hcmHouseCode, hcmJob: this.hcmJob, fscYear: this.fscYear });
    },

    setFscAccountStore: function (fscAccountStore) {
        var me = this;

        me.fscAccountStore = fscAccountStore;
    },

    addNewRecord: function (fscAccount) {
        var me = this;

        var records = me.queryBy(function (rec) {
            return rec.get('fscAccount') == fscAccount;
        });

        var record = records.get(0);
        if (!record) {
            var fscAccountRecord = me.fscAccountStore.getById(fscAccount);
            var title = !!fscAccountRecord ? fscAccountRecord.get('friendlyName') : String.format('Unknown "{0}"', fscAccount);
            var initData = me.initDataRecord(fscAccount, title);
            var isNegative = !!fscAccountRecord ? fscAccountRecord.get('isNegative') : false;

            record = me.newRecord(Ext.apply({ isNegative: isNegative }, initData));
            me.insert(me.getCount() - 1, [record]);
        }
    }

});

