        //                else
        //                    me._houseCodeComboBox.setValue(records[0].get('id'));
        //                me._jobCodeStore.load(records[0].get('id'));
        //            }
        //            else {
        //                me._houseCodeComboBox.setValue('');
        //            }
        //        }
        //        else {
