Bud.data.WORPeriodForecastStore = WebLight.extend(Bud.data.XmlStore, {

    hcmHouseCode: -1,
    hcmJob: -1,
    fscYear: -1,
    fscPeriod: -1,
    fscPeriodTitle: 0,

    accountStore: null,
    budDetailStore: null,
    budWorForecastStore: null,
    appJDEGLTransactionStore: null,
    _appJDEGLTransactionStore: null,

    isAnnualizedStore: false,

    constructor: function (config) {
        var me = this;

        config = Ext.apply(config || {}, { record: 'item',
            idProperty: 'fscAccount',
            fields: [
                { name: 'fscAccount', type: 'float' },
                { name: 'accCode' },
                { name: 'title' },
                { name: 'isNegative', type: 'bool' },
                { name: 'accountCategoryId', type: 'float' },
                { name: 'accountCategory' },
                { name: 'hcmHouseCode', type: 'float' },
                { name: 'hcmJob', type: 'float' },
                { name: 'fscYear', type: 'float' },
                { name: 'fscPeriod', type: 'float' },
                { name: 'week1', type: 'float' },
                { name: 'week2', type: 'float' },
                { name: 'week3', type: 'float' },
                { name: 'week4', type: 'float' },
                { name: 'summary', type: 'float' },
                { name: 'budget', type: 'float' },
                { name: 'budgetVariance', type: 'float' },
                { name: 'WORTotal', type: 'float' },
                { name: 'variance2WOR', type: 'float' },
                { name: 'orderIndex', type: 'float' }
            ]
        });

        Bud.data.WORPeriodForecastStore.superclass.constructor.call(this, config);

        this.on({
            add: { fn: this.calculateStore },
            remove: { fn: this.calculateStore },
            update: { fn: this.calculateStore }
        });

        this.budDetailStore = new Bud.data.BudDetailsStore();
        this.budWorForecastStore = new Bud.data.BudgetWORForecastStore();
        this.appJDEGLTransactionStore = new Bud.data.BudAppJDEGLTransactionStore();
        this._appJDEGLTransactionStore = new Bud.data.BudAppJDEGLTransactionStore();

        this.budDetailStore.on('load', me.subStoreLoaded.createDelegate(this));
        this.budWorForecastStore.on('load', me.subStoreLoaded.createDelegate(this));
        this.appJDEGLTransactionStore.on('load', me.subStoreLoaded.createDelegate(this));
    },

    clearStore: function () {
        var me = this;

        me.suspendEvents(false);

        // me.removeAll(); // can't use!
        me.remove(me.getRange());
        me._appJDEGLTransactionStore.removeAll();

        me.resumeEvents();
    },

    _loading: false,
    load: function (hcmHouseCode, hcmJob, fscYear, fscPeriod) {
        var me = this;
        if (me._loading)
            return;

        this.hcmHouseCode = hcmHouseCode;
        this.hcmJob = hcmJob;
        this.fscYear = fscYear;
        this.fscPeriod = fscPeriod;

        this._loadedStoreCount = 0;
        this.clearStore();

        this.budDetailStore.load(hcmHouseCode, hcmJob, fscYear);
        this.budWorForecastStore.load(hcmHouseCode, hcmJob, fscYear, fscPeriod);
        // specific period of current year
        this.appJDEGLTransactionStore.load(hcmHouseCode, hcmJob, fscYear, fscPeriod, 1);
    },

    reset: function () {
        var me = this;
        this.suspendEvents(false);
        this.remove(me.getRange());
        this.resumeEvents();
    },

    _calculating: false,
    calculateStore: function () {
        var me = this;

        if (me._calculating)
            return;

        me._calculating = true;

        var collection = me.queryBy(function (r, id) {
            return r.get('accCode') == '';
        });

        collection.each(function (item, index) {
            var categoryId = item.get('fscAccount');    // accountCategoryId

            var mix = me.queryBy(function (r, id) {
                return r.get('accountCategoryId') == categoryId && r.get('fscAccount') != categoryId;
            });


            var week1Total = 0;
            var week2Total = 0;
            var week3Total = 0;
            var week4Total = 0;
            var summaryTotal = 0;
            var budgetTotal = 0;

            mix.each(function (r, id) {
                var isNeg = r.get('isNegative');
                if (categoryId == 0)
                //total row, flip again
                    isNeg = !isNeg;
                week1Total += isNeg ? -r.get('week1') : r.get('week1');
                week2Total += isNeg ? -r.get('week2') : r.get('week2');
                week3Total += isNeg ? -r.get('week3') : r.get('week3');
                week4Total += isNeg ? -r.get('week4') : r.get('week4');
                summaryTotal += isNeg ? -r.get('summary') : r.get('summary');
                budgetTotal += isNeg ? -r.get('budget') : r.get('budget');
            });

            item.set('week1', me.decimal2(week1Total));
            item.set('week2', me.decimal2(week2Total));
            item.set('week3', me.decimal2(week3Total));
            item.set('week4', me.decimal2(week4Total));
            item.set('summary', me.decimal2(summaryTotal));
            item.set('budget', me.decimal2(budgetTotal));
        });

        var total = 0;
        this.each(function (record) {
            var fscAccount = record.get('fscAccount');
            total = record.get('week1') + record.get('week2') + record.get('week3') + record.get('week4');
            record.set('WORTotal', me.decimal2(total));
            // 2011/6/17
            //.  go to annual proj then click wor. pick housecode 1432/none/2012 period 10. as you can see budget variance = wor total - budget.. 
            // which is correct except revenue related accounts including revenue subtotal. 
            // for revenue related accounts and subtotal, we should flip the sign make budget variance = wor total - budget. 

            // 2011/6/20
            //revuen related accounts, budget variance = wor total - budget
            /// but other accounts budget variance = budget - wor total

            var budgetVariance = me.decimal2(record.get('budget') - total);
            var variance2WOR = me.decimal2(total - record.get('summary'));

            if (record.get('accountCategoryId') == 45 || record.get('fscAccount') == 45) {
                budgetVariance = -budgetVariance;
                // 2011/11/05
                variance2WOR = -variance2WOR;
            }
            // 2011/6/28
            if (fscAccount == 0)  // total row
            {

                budgetVariance = -budgetVariance;
                // 2010/10/21 variance2WOR no need flip again
                variance2WOR = -variance2WOR;
            }

            record.set('budgetVariance', budgetVariance);
            record.set('variance2WOR', variance2WOR);
        });

        me._calculating = false;
    },

    _loadedStoreCount: 0,
    subStoreLoaded: function () {
        var me = this;

        me._loadedStoreCount += 1;

        if (me._loadedStoreCount == 3) {
            me._loading = false;
            if (me.isDataLoaded()) {
                if (me.budDetailStore.getCount() == 0) {
                    //alert('Budget does not exist.');
                }
                else {
                    me.suspendEvents(false);
                    me.populateStore();
                    me.calculateStore();
                    me.resumeEvents();
                }
            }
            me.fireEvent('load', me);
        }
    },

    isDataLoaded: function () {
        var me = this;
        return me.accountStore.isDataLoaded() && me.budDetailStore.isDataLoaded() && me.budWorForecastStore.isDataLoaded();
    },

    calculateAppJDEGLTransactionStore: function (r) {
        var me = this;

        var records = this._appJDEGLTransactionStore.queryBy(function (rec) {
            return rec.get('hcmHouseCode') == r.get('hcmHouseCode')
                && rec.get('fscYear') == r.get('fscYear')
                && rec.get('fscPeriod') == r.get('fscPeriod')
                && rec.get('fscAccount') == r.get('fscAccount');
        });

        var record = records.get(0);

        if (!record) {
            this._appJDEGLTransactionStore.add(r);
        }
        else {
            var v = r.get('appJDEtAmount') + record.get('appJDEtAmount');
            record.set('appJDEtAmount', v);
        }
    },

    groupAppJDEGLTransactionStore: function () {
        var me = this;

        this.appJDEGLTransactionStore.each(function (record) {
            me.calculateAppJDEGLTransactionStore(record);
        });
    },

    getByFscAccount: function (fscAccount) {
        var me = this;

        //        var records = me.queryBy(function (rec) {
        //            return rec.get('fscAccount') == fscAccount;
        //        });

        //        var record = records.get(0);
        var record = me.getById(fscAccount);

        if (!record) {
            var fscAccountRecord = me.accountStore.getById(fscAccount);

            var initData = {
                fscAccount: fscAccount,
                title: !!fscAccountRecord ? fscAccountRecord.get('friendlyName') : String.format('Unknown "{0}"', fscAccount),
                accCode: !!fscAccountRecord ? fscAccountRecord.get('code') : 'Unknown',
                accountCategoryId: !!fscAccountRecord ? fscAccountRecord.get('accountCategoryId') : 0,
                accountCategory: !!fscAccountRecord ? fscAccountRecord.get('accountCategory') : '',
                //isNegative: !!fscAccountRecord ? fscAccountRecord.get('isNegative') : false,
                week1: 0, week2: 0, week3: 0, week4: 0, summary: 0,
                budget: 0, WORTotal: 0, budgetVariance: 0, variance2WOR: 0
            };

            record = me.newRecord(initData);
            me.add([record]);
        }

        return record;
    },

    populateStore: function () {
        var me = this;

        this._calculating = true;

        this.groupAppJDEGLTransactionStore();

        var i;


        this.budDetailStore.each(function (record) {
            var rec = me.getByFscAccount(record.get('fscAccount'));
            var total = 0;
            for (i = 0; i < 13; i++) {
                if (i + 1 == me.fscPeriodTitle)
                    total += record.get('period' + (i + 1));
            }
            rec.set('budget', me.decimal2(total));
        });


        this.budWorForecastStore.each(function (record) {
            var rec = me.getByFscAccount(record.get('fscAccount'));
            rec.set('week1', record.get('week1'));
            rec.set('week2', record.get('week2'));
            rec.set('week3', record.get('week3'));
            rec.set('week4', record.get('week4'));

            //2011/11/16
            rec.set('oldweek1', record.get('week1'));
            rec.set('oldweek2', record.get('week2'));
            rec.set('oldweek3', record.get('week3'));
            rec.set('oldweek4', record.get('week4'));
        });


        this._appJDEGLTransactionStore.each(function (record) {
            var rec = me.getByFscAccount(record.get('fscAccount'));
            rec.set('summary', record.get('appJDEtAmount'));
        });


        for (var i = 0; i < this.getCount(); i++) {
            var record = this.getAt(i);
            var fscAccount = me.accountStore.getById(record.get('fscAccount'));
            record.set('orderIndex', fscAccount.get('categoryDisplayOrder') * 10000 + parseFloat(fscAccount.get('code')));
        };

        //this.sort('accCode', 'ASC');
        //this.sort([{ field: 'accountCategoryId', direction: 'ASC' }, { field: 'title', direction: 'ASC'}], 'ASC');
        this.sort('orderIndex', 'ASC');


        for (var i = 0; i < this.getCount(); i++) {
            var record = this.getAt(i);
            var cateId = record.get('accountCategoryId');
            var cateName = record.get('accountCategory');

            var collection = me.queryBy(function (r) {
                return r.get('accountCategoryId') == cateId;
            });
            var length = collection.getCount();
            var init = {
                fscAccount: cateId,
                title: cateName,
                accCode: '',
                accountCategoryId: 0,
                accountCategory: '',
                isNegative: cateName == 'Revenue',
                week1: 0, week2: 0, week3: 0, week4: 0, summary: 0,
                budget: 0, WORTotal: 0, budgetVariance: 0, variance2WOR: 0
            }
            me.insert(i + length, me.newRecord(init));
            i += length;
        }

        this.add(me.newRecord({
            fscAccount: 0,
            title: 'Total',
            accCode: '',
            accountCategoryId: 0,
            accountCategory: '',
            week1: 0, week2: 0, week3: 0, week4: 0, summary: 0,
            budget: 0, WORTotal: 0, budgetVariance: 0, variance2WOR: 0
        }));

        this._calculating = false;
    },

    decimal2: function (v) {
        if (v && typeof v == 'number')
            return Math.round(v * 100) / 100;
        return v;
    },

    getCriteria: function () {
        return { hcmHouseCode: this.hcmHouseCode, hcmJob: this.hcmJob, fscYear: this.fscYear, fscPeriod: this.fscPeriod };
    },

    addAttributes: function (data) {
        Ext.apply(data, { hcmHouseCode: this.hcmHouseCode, hcmJob: this.hcmJob, fscYear: this.fscYear, fscPeriod: this.fscPeriod });
    },


    submitChanges: function (callback) {
        var me = this;
        this.fireEvent('beforesubmit', this);

        var worForecastStore = new Bud.data.BudgetWORForecastStore();
        worForecastStore.setContext(me.hcmHouseCode, me.hcmJob, me.fscYear, me.fscPeriod);

        Ext.each(this.getChangedRecords(), function (item, index) {
            if (item.get('accCode')) {
                //2011/11/16
                var week1 = 'N/A';
                var week2 = 'N/A';
                var week3 = 'N/A';
                var week4 = 'N/A';
                if (item.get('week1') != item.get('oldweek1')) {
                    week1 = item.get('week1');
                }

                if (item.get('week2') != item.get('oldweek2')) {
                    week2 = item.get('week2')
                }

                if (item.get('week3') != item.get('oldweek3')) {
                    week3 = item.get('week3');
                }

                if (item.get('week4') != item.get('oldweek4')) {
                    week4 = item.get('week4');
                }

                var data = {
                    fscAccount: item.get('fscAccount'),
                    week1: week1,
                    week2: week2,
                    week3: week3,
                    week4: week4
                };

                worForecastStore.add(worForecastStore.newRecord(data));
            }
        });

        worForecastStore.submitChanges(function () {
            if (callback)
                callback();
            me.fireEvent('submit');
            me.changes = [];
        });
    }
    ,

    addFscAccountToWor: function (hcmHouseCode, hcmJob, fscYear, fscPeriod, fscAccount, callback) {

        var xml = String.format('<transaction id="1"><budAddFscAccountToWor hcmHouseCode="{0}" hcmJob="{1}" fscYear="{2}" fscPeriod="{3}" fscAccount="{4}"/></transaction>',
                                        hcmHouseCode, hcmJob, fscYear, fscPeriod, fscAccount);
        var postData = String.format('moduleId={0}&requestId={1}&requestXml={2}&&targetId=iiTransaction',
            this.moduleId, this.requestId, encodeURIComponent(xml));

        jQuery.post(this.url, postData, function (data, status) {
            if (callback)
                callback(data, status);
            callback();
        });
    }
    ,

    copyFlashData: function (hcmHouseCode, hcmJob, fscYear, fscPeriod, week, callback) {

        var xml = String.format('<transaction id="1"><budWorCopyFlashData hcmHouseCode="{0}" hcmJob="{1}" fscYear="{2}" fscPeriod="{3}" week="{4}"/></transaction>',
                                        hcmHouseCode, hcmJob, fscYear, fscPeriod, week);
        var postData = String.format('moduleId={0}&requestId={1}&requestXml={2}&&targetId=iiTransaction',
            this.moduleId, this.requestId, encodeURIComponent(xml));

        jQuery.post(this.url, postData, function (data, status) {
            if (callback)
                callback(data, status);
            else
                callback();
        });
    }
});



